-- ----------------------------
-- Table structure for user_attend_info
-- ----------------------------
DROP TABLE IF EXISTS `user_attend_info`;
CREATE TABLE `user_attend_info`  (
  `id` int(11) UNSIGNED NOT NULL AUTO_INCREMENT COMMENT '自增主键',
  `channel` varchar(10) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT '' COMMENT '渠道',
  `language` varchar(10) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT '' COMMENT '语言',
  `generation` varchar(10) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT '' COMMENT '代数',
  `identification_code` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT '' COMMENT '玩家识别码',
  `wa_id` varchar(256) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT 'whatsappid',
  `rally_code` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT '' COMMENT '集结码',
  `cdk_code` varchar(256) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT '' COMMENT 'cdk',
  `red_packet_code` varchar(256) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT '' COMMENT '红包码',
  `attend_status` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT 'attend' COMMENT 'attend:参与活动；start_group：开团；over:助力成功',
  `created_at` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `updated_at` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  `attend_at` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '参与时间',
  `start_group_at` datetime NULL DEFAULT NULL COMMENT '开团时间',
  `red_packet_ready_at` datetime NULL DEFAULT NULL COMMENT '预发红包（召回）时间',
  `red_packet_send_at` datetime NULL DEFAULT NULL COMMENT '已发红包时间',
  `over_at` datetime NULL DEFAULT NULL COMMENT '助力成功时间',
  `activity_id` int(11) NOT NULL COMMENT '活动id',
  `extra` varchar(4096) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '用户参与消息内容',
  `user_nickname` varchar(256) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT '' COMMENT '用户昵称',
  `red_packet_status` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT 'un_red_packet' COMMENT 'un_red_packet:未发红包；red_packet_ready：预发红包；red_packet_send:已发红包；',
  `newest_help_at` datetime NULL DEFAULT CURRENT_TIMESTAMP COMMENT '最近助力时间(初始值是开团时间)',
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE INDEX `uk_wa_id`(`activity_id`, `wa_id`) USING BTREE,
  UNIQUE INDEX `uk_rally_code`(`activity_id`, `rally_code`) USING BTREE,
  UNIQUE INDEX `uk_identification_code`(`activity_id`, `identification_code`) USING BTREE,
  INDEX `inx_attend_status`(`activity_id`, `attend_status`, `red_packet_status`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 22 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '用户参与信息表' ROW_FORMAT = Dynamic;

